import React, { useState, useEffect } from 'react';
import './App.css';
import SearchIcon from './search.svg';
import MovieCard from './MovieCard.jsx';

// 10f5b6b7

const API_URL = 'http://www.omdbapi.com/?i=tt3896198&apikey=10f5b6b7';

const movie1 = 
  {
    "Title": "Italian Spiderman",
    "Year": "2007",
    "imdbID": "tt2705436",
    "Type": "movie",
    "Poster": "https://m.media-amazon.com/images/M/MV5BYjFhN2RjZTctMzA2Ni00NzE2LWJmYjMtNDAyYTllOTkyMmY3XkEyXkFqcGdeQXVyNTA0OTU0OTQ@._V1_SX300.jpg"
}


const App = () => {

  const [movies, setMovies] = useState([]);

  const [searchTerm, setSearchTerm] = useState('');

  const searchMovies = async (title) => {
    const response = await fetch(`${API_URL}&s=${title}`);
    const data = await response.json();
    setMovies(data.Search); 
  }
  useEffect(() => {
    searchMovies('Pokemon');
  }, [])

  return (
    <div className='app'>

    <h1 onClick={(e) => {setMovies({movie1});<MovieCard prop={movie1}/>}}>Not Netflix</h1>

    <div className="movie">
        <div>
          <p>{movie1.Year}</p>
        </div>
        <div>
          <img src={movie1.Poster !== 'N/A' ? prop.Poster : 'https://via.placeholder.com/400'} alt={prop.Title}/>;
        </div>
        <div>
          <span>{movie1.Type}</span>
          <h3>{movie1.Title}</h3>
        </div>

      </div> 
    <div className='search'>
      <input 
        placeholder='Search for movies' 
        value={searchTerm}
        onChange={(e) => {setSearchTerm(e.target.value)}}
        onClick={(e) => {searchMovies(searchTerm)}}>

      </input>

        <img  
          src={SearchIcon}
          alt="search"
          onClick={(e) => {searchMovies(searchTerm)}}
        />
    </div>

{
  movies?.length > 0 
    ? (<div className="container">
      {movies.map((movies) => (
        <MovieCard prop={movies}/>
      ))}
      </div>

    ) : (
      <div className="empty">
        <h2>No movies found</h2>
        </div>

    )
}


    </div>
  );
}

export default App;
